import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const roleConfig = {
  student: {
    title: 'Student Dashboard',
    kpis: [
      { label: 'Points', value: '250' },
      { label: 'Level', value: '5' },
      { label: 'Day Streak', value: '7' },
      { label: 'Badges', value: '3' }
    ],
    sections: [
      {
        title: 'My Games & Puzzles',
        items: [
          { name: 'Addition Adventure', type: 'math', status: 'Completed', points: '+10' },
          { name: 'Multiplication Master', type: 'math', status: 'LOCKED', points: '+30' },
          { name: 'Science Quiz', type: 'quiz', status: 'Completed', points: '+15' },
          { name: 'Word Matching', type: 'matching', cta: 'PLAY NOW', points: '+20' }
        ]
      }
    ]
  },
  teacher: {
    title: 'Teacher Dashboard',
    kpis: [
      { label: 'Total Students', value: '120' },
      { label: 'Avg Progress', value: '72%' },
      { label: 'Weekly Growth', value: '+8%' },
      { label: 'Engagement', value: '85%' }
    ],
    sections: [
      {
        title: 'Classroom Overview',
        table: {
          headers: ['Student Name', 'Progress', 'Status', 'Weak Area', 'Last Active'],
          rows: [
            ['Rahul Kumar', '75%', 'active', 'Algebra', '2 hours ago'],
            ['Priya Sharma', '85%', 'active', '—', '1 hour ago'],
            ['Amit Patel', '60%', 'inactive', 'Geometry', '1 day ago'],
            ['Neha Verma', '92%', 'active', '—', '30 min ago'],
            ['Raj Singh', '45%', 'struggling', 'Fractions', '3 days ago']
          ]
        }
      }
    ]
  },
 admin: {
    title: 'School Administration',
    kpis: [
      { label: 'Total Students', value: '450' },
      { label: 'Teachers', value: '25' },
      { label: 'Courses', value: '12' },
      { label: 'Classes', value: '18' },
      { label: 'Performance', value: '75%' }
    ],
    sections: [
      {
        title: 'Quick Actions',
        actions: [
          { label: 'Add Teacher', color: 'from-purple-500 to-blue-500' },
          { label: 'Enroll Student', color: 'from-green-500 to-emerald-600' },
          { label: 'Add Course', color: 'from-amber-500 to-orange-600' },
          { label: 'Export Report', color: 'from-gray-500 to-gray-600' }
        ]
      }
    ]
  }
};

const Badge = ({ label, value }) => (
  <div className="card text-center">
    <div className="text-2xl font-bold mb-1">{value}</div>
    <div className="text-sm text-gray-500">{label}</div>
  </div>
);

const Dashboard = () => {
  const { role } = useParams();
  const navigate = useNavigate();
  const cfg = roleConfig[role] || roleConfig.student;

  return (
    <div className="min-h-screen bg-dark-900 bg-space-gradient p-6 text-slate-100">
      {/* Top bar */}
      <div className="container mx-auto mb-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold text-white">{cfg.title}</h1>
            <p className="text-sm text-slate-400">Welcome back!</p>
          </div>
          <div className="flex items-center gap-3">
            <button onClick={() => navigate('/')} className="btn btn-outline">Home</button>
            <button className="btn btn-primary shadow-glow">Download for Offline</button>
            <button className="btn" style={{background:'linear-gradient(135deg,#22f3a2,#3b82f6)',color:'#0b1020'}}>Profile</button>
          </div>
        </div>
      </div>

      {/* KPIs */}
      <div className="container mx-auto grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
        {cfg.kpis.map((k) => (
          <Badge key={k.label} label={k.label} value={k.value} />
        ))}
      </div>

      {/* Sections */}
      <div className="container mx-auto grid gap-6">
        {cfg.sections.map((section) => (
          <div key={section.title} className="card">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-lg font-semibold text-white">{section.title}</h2>
            </div>

            {/* Cards list for student */}
            {section.items && (
              <div className="grid md:grid-cols-4 gap-4">
                {section.items.map((item) => (
                  <div key={item.name} className="border border-slate-700 bg-dark-800 rounded-lg p-4">
                    <div className="text-sm text-slate-400 mb-2">{item.type}</div>
                    <div className="font-semibold mb-4 text-slate-100">{item.name}</div>
                    <div className="flex items-center justify-between text-sm">
                      <span className="text-slate-400">{item.points}</span>
                      {item.cta ? (
                        <button className="btn btn-primary py-1 px-3 shadow-glow">{item.cta}</button>
                      ) : (
                        <span className="text-slate-500">{item.status}</span>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            )}

            {/* Table for teacher */}
            {section.table && (
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead>
                    <tr className="text-left text-slate-400 text-sm">
                      {section.table.headers.map((h) => (
                        <th key={h} className="pb-2 pr-6 font-medium">{h}</th>
                      ))}
                    </tr>
                  </thead>
                  <tbody>
                    {section.table.rows.map((row, idx) => (
                      <tr key={idx} className="border-t border-slate-700">
                        {row.map((cell, cidx) => (
                          <td key={cidx} className="py-3 pr-6 text-sm text-slate-200">{cell}</td>
                        ))}
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}

            {/* Actions for admin */}
            {section.actions && (
              <div className="grid md:grid-cols-4 gap-3">
                {section.actions.map((a) => (
                  <button key={a.label} className={`btn text-white bg-gradient-to-r ${a.color} shadow-glow`}>
                    {a.label}
                  </button>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
